void TMR1_IRQHandler(void)
{
	OpenSevenSegment();
	if (state == 0)
	{
		PA12 = 1;
		PA13 = 0;
		PA14 = 1;
		ShowSevenSegment(0,9 - green_timer % 9);
		green_timer++;
		if (green_timer == 9)
			state = 1;
		
		if(frame % 3 + 1 == 1)
		{
			draw_LCD(green1);
		}else if(frame % 3 + 1 == 2)
		{
			draw_LCD(green2);
		}
		else if(frame % 3 + 1 == 3)
		{
			draw_LCD(green3);
		}
		frame++;
	}
	else
	{
		PA12 = 1;
		PA13 = 1;
		PA14 = 0;
		ShowSevenSegment(3,5 - red_timer % 5);
		red_timer++;
		if (red_timer == 5)
			state = 0;
		
		draw_LCD(red1);
	}

	TIMER_ClearIntFlag(TIMER1);
}

