{
	TIMER_Open(TIMER1, TMR1_OPERATING_MODE, TMR1_OPERATING_FREQ);
	TIMER_EnableInt(TIMER1);
	NVIC_EnableIRQ(TMR1_IRQn);
	TIMER_Start(TIMER1);
}

void EINT1_IRQHandler(void)
{
	state = 1;
	red_timer = 0;
	green_timer = 0;
	buzz();
	GPIO_CLR_INT_FLAG(PB, BIT15);
}

